mapper.select:
SELECT C.CORP_NO,C.CORP_IMG,C.C_NAME, C.ADDR,C.C_BOSS, C.SECTOR_NAME FROM ( SELECT CI.CORP_NO,CI.CORP_IMG,CI.C_NAME, CI.ADDR,CI.C_BOSS,S.SECTOR_NAME, ROW_NUMBER()OVER(ORDER BY CI.CORP_NO DESC) AS ENUM FROM C_INFO CI JOIN C_SECTOR CS ON CI.CORP_NO = CS.CORP_NO JOIN SECTOR S ON CS.SECTOR_NO = S.SECTOR_NO WHERE CI.WRK_CNT >= 1
AND CI.C_NAME LIKE '%' || #{searchTxt} || '%' OR S.SECTOR_NAME LIKE '%' || #{searchTxt} || '%'
AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
AND S.SECTOR_NAME LIKE '%' || #{searchTxt} || '%'
)C WHERE C.ENUM BETWEEN #{startCnt} AND #{endCnt}
