mapper.select:
		SELECT COUNT(DISTINCT EA.EMP_NO) AS CNT
		FROM EMP_ANNC EA INNER JOIN C_MEMBER CM
		ON EA.MEM_NO = CM.MEM_NO
		INNER JOIN C_INFO CI
		ON CM.CORP_NO = CI.CORP_NO
		FULL OUTER JOIN(SELECT DISTINCT QUAL_NO,EMP_NO FROM EMP_QUAL) EQ
		ON EA.EMP_NO = EQ.EMP_NO
		WHERE DEL_CHK = 0
		
			
				AND EA.SECTOR_NO = #{sectorNo}
			
			
				AND REG_DATE >= #{startDate}
			
			
				AND DLINE <= #{endDate}
			

			
				AND REGION_NO = #{region}
			
			
				
					
						AND PAY_GBN = '0'
					
					
						AND PAY_GBN = '1'
						AND PAY_LOW >= #{minSal}
					
					
						AND PAY_GBN = '1'
						AND PAY_HIGH <= #{maxSal}
					
				
			
			
				AND EMP_GBN = #{empGbn}
			
			
				AND GRADU = #{gradu}  
			
			
			    AND QUAL_NO IN (${qualno}) 

			
			
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
					OR
					EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
				
				
					AND EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
			

		




		
			AND EA.SECTOR_NO = #{sectorNo}
		
		
			AND REG_DATE >= #{startDate}
		
		
			AND DLINE <= #{endDate}
		
		
			AND REGION_NO = #{region}
		
		
			
				
					AND PAY_GBN = '0'
				
				
					AND PAY_GBN = '1'
					AND PAY_LOW >= #{minSal}
				
				
					AND PAY_GBN = '1'
					AND PAY_HIGH <= #{maxSal}
				
			
		
		
			
				
					AND CARR = '0'
				
				
					AND CARR = '1'
				
				
					AND CARR = '2'
					AND CARR_Y >= #{minCareer}
				
				
					AND CARR = '2'
					AND CARR_Y <= #{maxCareer}
				
			
		
		
			AND EMP_GBN = #{empGbn}
		
		
			AND GRADU = #{gradu}
		
		
		    AND QUAL_NO IN (${qualno})

		SELECT E.EMP_NO, E.CORP_IMG, E.C_NAME, E.EMP_TITLE, SECTOR_NAME, DLINE
		FROM (SELECT EA.EMP_NO, CI.CORP_IMG, CI.C_NAME, EA.EMP_TITLE, SECTOR_NAME, TO_CHAR(DLINE,'YY/MM/DD') DLINE, 
        ROW_NUMBER() OVER(ORDER BY EA.EMP_NO DESC) AS ENUM
		FROM EMP_ANNC EA INNER JOIN C_MEMBER CM 
        ON EA.MEM_NO = CM.MEM_NO
        JOIN SECTOR S
        ON (EA.SECTOR_NO = S.SECTOR_NO)
		INNER JOIN C_INFO CI
        ON CM.CORP_NO = CI.CORP_NO 
        FULL OUTER JOIN(SELECT DISTINCT QUAL_NO,EMP_NO FROM EMP_QUAL) EQ
        ON EA.EMP_NO = EQ.EMP_NO
		WHERE EA.DEL_CHK = 0
		
			
				AND EA.SECTOR_NO = #{sectorNo}
			
			
				AND REG_DATE >= #{startDate}
			
			
				AND DLINE <= #{endDate}
			

			
				AND REGION_NO = #{region}
			
			
				
					
						AND PAY_GBN = '0'
					
					
						AND PAY_GBN = '1'
						AND PAY_LOW >= #{minSal}
					
					
						AND PAY_GBN = '1'
						AND PAY_HIGH <= #{maxSal}
					
				
			
			
				AND EMP_GBN = #{empGbn}
			
			
				AND GRADU = #{gradu}
			
			
			    AND QUAL_NO IN (${qualno})
			

			
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
					OR
					EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
				
				
					AND EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
			

		




		
			AND EA.SECTOR_NO = #{sectorNo}
		
		
			AND REG_DATE >= #{startDate}
		
		
			AND DLINE <= #{endDate}
		
		
			AND REGION_NO = #{region}
		
		
			
				
					AND PAY_GBN = '0'
				
				
					AND PAY_GBN = '1'
					AND PAY_LOW >= #{minSal}
				
				
					AND PAY_GBN = '1'
					AND PAY_HIGH <= #{maxSal}
				
			
		
		
			
				
					AND CARR = '0'
				
				
					AND CARR = '1'
				
				
					AND CARR = '2'
					AND CARR_Y >= #{minCareer}
				
				
					AND CARR = '2'
					AND CARR_Y <= #{maxCareer}
				
			
		
		
			AND EMP_GBN = #{empGbn}
		
		
			AND GRADU = #{gradu}
		
		
		    AND QUAL_NO IN (${qualno})
		

		)E
		WHERE E.ENUM BETWEEN #{startCnt} AND #{endCnt}
