mapper.select:
SELECT COUNT(DISTINCT R.RESUM_NO) AS CNT FROM RESUM R JOIN MEMBER M ON R.MEM_NO = M.MEM_NO JOIN RESUM_LOC RL ON (R.RESUM_NO = RL.RESUM_NO) JOIN SECTOR S ON (R.SECTOR_NO = S.SECTOR_NO) JOIN REGION RG ON (R.REGION_NO = RG.REGION_NO) JOIN CITY C ON (RG.CITY_NO = C.CITY_NO) JOIN RESUM_WORK RW ON (R.RESUM_NO = RW.RESUM_NO) FULL OUTER JOIN RESUM_QUAL RQ ON (R.RESUM_NO = RQ.RESUM_NO) WHERE R.DEL_CHK = 0 AND OPN_CHK = 1
AND S.SECTOR_NO = #{sectorNo}
AND RG.REGION_NO = #{region}
AND PAY_GBN = '0'
AND PAY_GBN = '1' AND PAY_LOW >= #{minSal}
AND PAY_GBN = '1' AND PAY_HIGH <= #{maxSal}
AND RW.WORK_TYPE = #{work_type}
AND GRADU = #{gradu}
AND QUAL_NO IN (${qualno})
AND M.NAME LIKE '%' || #{searchTxt} || '%' OR SECTOR_NAME LIKE '%' || #{searchTxt} || '%' OR CITY_NAME LIKE '%' || #{searchTxt} || '%' OR REGION_NAME LIKE '%' || #{searchTxt} || '%'
AND M.NAME LIKE '%' || #{searchTxt} || '%'
AND SECTOR_NAME LIKE '%' || #{searchTxt} || '%'
AND CITY_NAME LIKE '%' || #{searchTxt} || '%' AND REGION_NAME LIKE '%' || #{searchTxt} || '%'
AND S.SECTOR_NO = #{sectorNo}
AND RG.REGION_NO = #{region}
AND PAY_GBN = '0'
AND PAY_GBN = '1' AND PAY_LOW >= #{minSal}
AND PAY_GBN = '1' AND PAY_HIGH <= #{maxSal}
AND RW.WORK_TYPE = #{work_type}
AND GRADU = #{gradu}
AND QUAL_NO IN (${qualno})

SELECT H.RESUM_NO,H.NAME,SECTOR_NAME,CITY_NAME,REGION_NAME,H.CARR, H.REG_DATE FROM ( SELECT R.MEM_NO, R.RESUM_NO , M.NAME,SECTOR_NAME,CITY_NAME,REGION_NAME, R.CARR, TO_CHAR(R.REG_DATE,'YYYY-MM-DD') REG_DATE, ROW_NUMBER()OVER(ORDER BY R.MEM_NO ASC) AS ENUM FROM RESUM R JOIN MEMBER M ON R.MEM_NO = M.MEM_NO
JOIN ( SELECT DISTINCT RESUM_NO FROM RESUM_LOC ) RL ON (R.RESUM_NO = RL.RESUM_NO)
JOIN ( SELECT DISTINCT RESUM_NO FROM RESUM_QUAL )RQ ON (R.RESUM_NO = RQ.RESUM_NO)
JOIN ( SELECT SECTOR_NAME,SECTOR_NO FROM SECTOR ) S ON (R.SECTOR_NO = S.SECTOR_NO)
JOIN ( SELECT REGION_NO, REGION_NAME, CITY_NO FROM REGION ) RG ON (R.REGION_NO = RG.REGION_NO)
JOIN CITY C ON (RG.CITY_NO = C.CITY_NO)
JOIN ( SELECT DISTINCT RESUM_NO FROM RESUM_WORK )RW ON (R.RESUM_NO = RW.RESUM_NO)
WHERE R.DEL_CHK = 0 AND R.OPN_CHK = 1
AND S.SECTOR_NO = #{sectorNo}
AND RG.REGION_NO = #{region}
AND PAY_GBN = '0'
AND PAY_GBN = '1' AND PAY_LOW >= #{minSal}
AND PAY_GBN = '1' AND PAY_HIGH <= #{maxSal}
AND RW.WORK_TYPE = #{work_type}
AND GRADU = #{gradu}
AND QUAL_NO IN (${qualno})
AND M.NAME LIKE '%' || #{searchTxt} || '%' OR SECTOR_NAME LIKE '%' || #{searchTxt} || '%' OR CITY_NAME LIKE '%' || #{searchTxt} || '%' OR REGION_NAME LIKE '%' || #{searchTxt} || '%'
AND M.NAME LIKE '%' || #{searchTxt} || '%'
AND SECTOR_NAME LIKE '%' || #{searchTxt} || '%'
AND CITY_NAME LIKE '%' || #{searchTxt} || '%' OR REGION_NAME LIKE '%' || #{searchTxt} || '%'
AND S.SECTOR_NO = #{sectorNo}
AND RG.REGION_NO = #{region}
AND PAY_GBN = '0'
AND PAY_GBN = '1' AND PAY_LOW >= #{minSal}
AND PAY_GBN = '1' AND PAY_HIGH <= #{maxSal}
AND RW.WORK_TYPE = #{work_type}
AND GRADU = #{gradu}
AND QUAL_NO IN (${qualno})
)H WHERE H.ENUM BETWEEN #{startCnt} AND #{endCnt}
