mapper.select:
		SELECT COUNT(*) AS CNT
		FROM RESUM R JOIN MEMBER M
        ON R.MEM_NO = M.MEM_NO
		WHERE DEL_CHK = 0
		
			
				AND SECTOR_NO = #{sectorNo}
			


			
				AND REGION_NO = #{region}
			
			
				
					
						AND PAY_GBN = '0'
					
					
						AND PAY_GBN = '1'
						AND PAY_LOW >= #{minSal}
					
					
						AND PAY_GBN = '1'
						AND PAY_HIGH <= #{maxSal}
					
				
			
			
				AND EMP_GBN = #{empGbn}
			
			
				AND GRADU = #{gradu}
			
			
			    AND QUAL_NO IN  
				
				        #{qualno}
				 
			

			
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
					OR
					EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
				
				
					AND EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
			

		




		
			AND SECTOR_NO = #{sectorNo}
		

		
			AND REGION_NO = #{region}
		
		
			
				
					AND PAY_GBN = '0'
				
				
					AND PAY_GBN = '1'
					AND PAY_LOW >= #{minSal}
				
				
					AND PAY_GBN = '1'
					AND PAY_HIGH <= #{maxSal}
				
			
		
		
			
				
					AND CARR = '0'
				
				
					AND CARR = '1'
				
				
					AND CARR = '2'
					AND CARR_Y >= #{minCareer}
				
				
					AND CARR = '2'
					AND CARR_Y <= #{maxCareer}
				
			
		
		
			AND EMP_GBN = #{empGbn}
		
		
			AND GRADU = #{gradu}
		
			
			    AND QUAL_NO IN  
				
				        #{qualno}

		SELECT H.NAME,SECTOR_NAME,CITY_NAME,REGION_NAME,H.CARR, H.REG_DATE
		FROM (SELECT
		M.NAME,SECTOR_NAME,CITY_NAME,REGION_NAME,
        R.CARR, TO_CHAR(R.REG_DATE, 'YYYY-MM-DD') REG_DATE,
		ROW_NUMBER()OVER(ORDER BY R.MEM_NO DESC) AS ENUM
		FROM RESUM R 
        JOIN MEMBER M
        ON R.MEM_NO = M.MEM_NO
        JOIN RESUM_LOC RL
        ON (R.RESUM_NO = RL.RESUM_NO)
        JOIN SECTOR S
        ON (R.SECTOR_NO = S.SECTOR_NO)
        
        JOIN REGION RG
        ON (R.REGION_NO = RG.REGION_NO)
        JOIN CITY C
        ON (RG.CITY_NO = C.CITY_NO)
        WHERE R.DEL_CHK = 0
		
			
				AND SECTOR_NO = #{sectorNo}
			
			
				AND REG_DATE >= #{startDate}
			
			
				AND DLINE <= #{endDate}
			

			
				AND REGION_NO = #{region}
			
			
				
					
						AND PAY_GBN = '0'
					
					
						AND PAY_GBN = '1'
						AND PAY_LOW >= #{minSal}
					
					
						AND PAY_GBN = '1'
						AND PAY_HIGH <= #{maxSal}
					
				
			
			
				AND EMP_GBN = #{empGbn}
			
			
				AND GRADU = #{gradu}
			
			
			    AND QUAL_NO IN  
				
				        #{qualno}
				 
			

			
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
					OR
					EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
				
					AND CI.C_NAME LIKE '%' || #{searchTxt} || '%'
				
				
					AND EA.EMP_TITLE LIKE '%' || #{searchTxt} || '%'
				
			

		




		
			AND SECTOR_NO = #{sectorNo}
		
		
			AND REG_DATE >= #{startDate}
		
		
			AND DLINE <= #{endDate}
		
		
			AND REGION_NO = #{region}
		
		
			
				
					AND PAY_GBN = '0'
				
				
					AND PAY_GBN = '1'
					AND PAY_LOW >= #{minSal}
				
				
					AND PAY_GBN = '1'
					AND PAY_HIGH <= #{maxSal}
				
			
		
		
			
				
					AND CARR = '0'
				
				
					AND CARR = '1'
				
				
					AND CARR = '2'
					AND CARR_Y >= #{minCareer}
				
				
					AND CARR = '2'
					AND CARR_Y <= #{maxCareer}
				
			
		
		
			AND EMP_GBN = #{empGbn}
		
		
			AND GRADU = #{gradu}
		
			
			    AND QUAL_NO IN  
				
				        #{qualno}
				 
			
		)H
		WHERE E.ENUM BETWEEN #{startCnt} AND #{endCnt}
