mapper.select:
SELECT DCLARE_NO, NAME, MEM_NO, EMP_NO, EMP_TITLE, DCLARE_CODE, DCLARE_REASN, DCLARE_REFUSE, REG_DATE, PRCSS_DATE, C_NAME, PRCSS_GBN, DEL_CHK FROM ( SELECT DCLARE_NO, NAME, ED.MEM_NO, ED.EMP_NO, EMP_TITLE, DCLARE_CODE, DCLARE_REASN, DCLARE_REFUSE, TO_CHAR(ED.REG_DATE,'YYYY-MM-DD') REG_DATE, TO_CHAR(PRCSS_DATE,'YYYY-MM-DD') PRCSS_DATE, C_NAME, PRCSS_GBN, EA.DEL_CHK, ROW_NUMBER() OVER(ORDER BY DCLARE_NO) RNUM FROM EMP_DCLARE ED JOIN MEMBER M ON (ED.MEM_NO = M.MEM_NO) JOIN EMP_ANNC EA ON (ED.EMP_NO = EA.EMP_NO) JOIN C_MEMBER CM ON (EA.MEM_NO = CM.MEM_NO) JOIN C_INFO CI ON (CM.CORP_NO = CI.CORP_NO) WHERE ED.DEL_CHK = 0
AND PRCSS_GBN = 0 AND EA.DEL_CHK = 0
) ED WHERE ED.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE EMP_DCLARE SET
DCLARE_REFUSE = #{dclareRefuse},
PRCSS_GBN = #{prcssGbn}, PRCSSMEM_NO = #{manageNo}, PRCSS_DATE = SYSDATE WHERE DEL_CHK = 0 AND DCLARE_NO = #{dclareNo}

mapper.select:
SELECT GRADE_NO, APLMEM_NO, NAME, CORP_NO, C_NAME, WLB, PROMO_POSS, C_CULT, WFARE_PAY, MGM, EVAL, REFUSE_REASN, PRCSS_GBN, REG_DATE, PRCSS_DATE FROM ( SELECT GRADE_NO, APLMEM_NO, NAME, G.CORP_NO, C_NAME, WLB, PROMO_POSS, C_CULT, WFARE_PAY, MGM, EVAL, REFUSE_REASN, PRCSS_GBN, TO_CHAR(REG_DATE,'YYYY-MM-DD') REG_DATE, TO_CHAR(PRCSS_DATE,'YYYY-MM-DD') PRCSS_DATE, M.WDRAWL_DATE, ROW_NUMBER() OVER(ORDER BY GRADE_NO) RNUM FROM GRADE G JOIN MEMBER M ON (G.APLMEM_NO = M.MEM_NO) JOIN C_INFO CI ON (G.CORP_NO = CI.CORP_NO) WHERE DEL_CHK = 0
AND PRCSS_GBN = 0
) G WHERE G.RNUM BETWEEN #{startCnt} AND #{endCnt} ORDER BY REG_DATE

mapper.update:
UPDATE GRADE SET
REFUSE_REASN = #{refuseReasn},
PRCSS_GBN = #{prcssGbn}, PRCSSMEM_NO = #{manageNo}, PRCSS_DATE = SYSDATE WHERE DEL_CHK = 0 AND GRADE_NO = #{gradeNo}
