mapper.select:
SELECT N.NOTICE_NO, N.NOTICE_GBN, N.NOTICE_TITLE, N.NAME, N.REG_DATE FROM(SELECT N.NOTICE_NO, N.NOTICE_GBN, N.NOTICE_TITLE, M.NAME, TO_CHAR(N.REG_DATE,'YYYY-MM-DD') AS REG_DATE, ROW_NUMBER() OVER(ORDER BY N.NOTICE_NO DESC) AS RNUM FROM NOTICE N INNER JOIN MEMBER M ON N.MEM_NO = M.MEM_NO WHERE N.DEL_CHK = 0
AND N.NOTICE_GBN LIKE '%' || #{searchTxt} || '%' OR N.NOTICE_TITLE LIKE '%' || #{searchTxt} || '%'
AND N.NOTICE_GBN LIKE '%' || #{searchTxt} || '%'
AND N.NOTICE_TITLE LIKE '%' || #{searchTxt} || '%'
) N WHERE N.RNUM BETWEEN #{startCnt} AND #{endCnt}
